mapper.select:
SELECT c.*,cs.coupon_name,cs.par_value FROM tbl_coupon c LEFT JOIN tbl_coupon_scheme cs 
ON c.coupon_scheme_id=cs.id where 1=1
 and coupon_scheme_id = #{couponSchemeId} 
and state=#{state} 
and coupon_number like CONCAT(#{couponNumber},'%')

SELECT count(1) FROM tbl_coupon where 1=1
 and coupon_scheme_id = #{couponSchemeId} 
and state=#{state} 
and coupon_number like CONCAT(#{couponNumber},'%')

mapper.update:
update tbl_coupon
state = #{state,jdbcType=SMALLINT}, use_startdate = #{useStartdate,jdbcType=TIMESTAMP}, use_enddate = #{useEnddate,jdbcType=TIMESTAMP}, use_time = #{useTime,jdbcType=TIMESTAMP}, use_order_number = #{useOrderNumber,jdbcType=VARCHAR}
where id = #{id}
