mapper.select:
  		SELECT *from tbl_order_main where 1=1
  		and order_main_no like CONCAT('%',#{orderMainNo},'%') 
  		and coupon_number like CONCAT('%',#{couponNumber},'%') 
  		and consignee=#{consignee} 
  		and order_date=#{orderDate} 
  		and state=#{state} 
  		and create_time >= #{startOrderTime} 
  		and create_time <= #{endOrderTime} 
  		and is_print_express=#{isPrintExpress} 
		order by create_time desc

SELECT count(1) FROM tbl_order_main where 1=1
and order_main_no like CONCAT('%',#{orderMainNo},'%') 
and coupon_number like CONCAT('%',#{couponNumber},'%') 
and consignee=#{consignee} 
and order_date=#{orderDate} 
and state=#{state} 
and create_time >= #{startOrderTime} 
and create_time <= #{endOrderTime} 
and is_print_express=#{isPrintExpress}

mapper.update:
update tbl_order_main
state = #{state,jdbcType=SMALLINT}, consignee = #{consignee,jdbcType=VARCHAR}, mobile_phone = #{mobilePhone,jdbcType=VARCHAR}, province_code = #{provinceCode,jdbcType=VARCHAR}, city_code = #{cityCode,jdbcType=VARCHAR}, area_code = #{areaCode,jdbcType=VARCHAR}, province_city_area = #{provinceCityArea,jdbcType=VARCHAR}, detail_address = #{detailAddress,jdbcType=VARCHAR}, logistics_company_code = #{logisticsCompanyCode,jdbcType=VARCHAR}, logistics_company = #{logisticsCompany,jdbcType=VARCHAR}, express_order_no = #{expressOrderNo,jdbcType=VARCHAR}, deliver_time = #{deliverTime,jdbcType=TIMESTAMP}, is_send_sms = #{isSendSms,jdbcType=SMALLINT}, and is_print_express=#{isPrintExpress}
where id = #{id}
